mapper.select:
select

from `registry_record` rr

where 1=1



        and rr.`town_name` like concat('%', #{object.townName}, '%') = #{object.townName}


        and rr.`street_name` like concat('%', #{object.streetName}, '%') = #{object.streetName}


        and rr.`building_number` like concat('%', #{object.buildingNumber}, '%') = #{object.buildingNumber}


        and rr.`apartment_number` like concat('%', #{object.apartmentNumber}, '%') = #{object.apartmentNumber}



        and rr.`first_name` like concat('%', #{object.firstName}, '%') = #{object.firstName}


        and rr.`last_name` like concat('%', #{object.lastName}, '%') = #{object.lastName}


        and rr.`middle_name` like concat('%', #{object.middleName}, '%') = #{object.middleName}



        and rr.`status` = #{object.status.id}




    limit ${first},${count}
